chartistLineCode:
import { Component } from '@angular/core'
import * as Chartist from 'chartist';
import { ChartType, ChartEvent } from "ng-chartist/dist/chartist.component";

export interface Chart {
    type: ChartType;
    data: Chartist.IChartistData;
    options?: any;
    responsiveOptions?: any;
    events?: ChartEvent;
}

@Component({
    template: `
        <x-chartist 
            class="ct-chart"
            [data]="lineChart.data" 
            [type]="lineChart.type" 
            [options]="lineChart.options">
        </x-chartist>
    `
})

export class ChartistDemoComponent {
    
    //Line Chart
    lineChart: Chart = {
        type: 'Line',
        data: {
            "labels": [ 'Monday', 'Tuesday', 'Wednesday', 'Thursday'
            ],
            "series": [
                [2, 11, 6, 8, 15],
                [2, 8, 3, 4, 9]
            ]
        },
        options: {
            low: 0,
            fullWidth: true,
            onlyInteger: true,
            axisY: {
                low: 0,
                scaleMinSpace: 50,
            },
            axisX: {
                showGrid: false
            }
        }
    }
}

chartistLineScatterCode:
import { Component } from '@angular/core'
import * as Chartist from 'chartist';
import { ChartType, ChartEvent } from "ng-chartist/dist/chartist.component";

let chartistData = require('../../../assets/data/charts/chartist.json');
declare var require: any

export interface Chart {
    type: ChartType;
    data: Chartist.IChartistData;
    options?: any;
    responsiveOptions?: any;
    events?: ChartEvent;
}

@Component({
    template: `
        <x-chartist 
            class="ct-chart"
            [data]="lineScatterChart.data" 
            [type]="lineScatterChart.type" 
            [options]="lineScatterChart.options" 
            [responsiveOptions]="lineScatterChart.responsiveOptions"
            [events]="lineScatterChart.events">
        </x-chartist>
    `
})

export class ChartistDemoComponent {

    //Line Scatter Chart
    lineScatterChart: Chart = {
        type: 'Line',
        data: chartistData ['lineScatterChartData'],
        options: {
            showLine: false,
            axisX: {
                labelInterpolationFnc: function (value: number, index: number): string {
                    return index % 13 === 0 ? `W${value}` : null;
                },
                showGrid: false
            },
            axisY: {
                scaleMinSpace: 30,
            }
        },
        responsiveOptions: [
            [
                'screen and (min-width: 640px)',
                {
                    axisX: {
                        labelInterpolationFnc: function (value: number, index: number): string {
                            return index % 4 === 0 ? `W${value}` : null;
                        }
                    }
                }
            ]
        ]
    }
}

