carouselBasicCode:
import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'nz-demo-carousel-basic',
    template: `
        <nz-carousel [nzEffect]="effect">
            <div nz-carousel-content *ngFor="let index of array">
                <h3 class="text-white">{{index}}</h3>
            </div>
        </nz-carousel>`,
    styles  : [
        `[nz-carousel-content] {
            text-align: center;
            height: 160px;
            line-height: 160px;
            background: #364d79;
            color: #fff;
            overflow: hidden;
        }

        [nz-carousel-content] h3 {
            height: 160px;
            line-height: 160px;
        }    
    `]
})
export class NzDemoCarouselBasicComponent implements OnInit {
    array = [ 1, 2, 3, 4 ];
    effect = 'scrollx';

    ngOnInit() {
        setTimeout(() => {
            this.effect = 'fade';
        }, 3000);
    }
}

carouselPositionCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-carousel-position',
    template: `
        <nz-radio-group [(ngModel)]="dotPosition">
            <label nz-radio-button nzValue="bottom">Bottom</label>
            <label nz-radio-button nzValue="top">Top</label>
            <label nz-radio-button nzValue="left">Left</label>
            <label nz-radio-button nzValue="right">Right</label>
        </nz-radio-group>
        <nz-carousel [nzDotPosition]="dotPosition">
            <div nz-carousel-content *ngFor="let index of array">
                <h3>{{ index }}</h3>
            </div>
        </nz-carousel>
    `,
    styles: [
        `
        nz-radio-group {
            margin-bottom: 8px;
        }

        [nz-carousel-content] {
            text-align: center;
            height: 160px;
            line-height: 160px;
            background: #364d79;
            color: #fff;
            overflow: hidden;
        }

        h3 {
            color: #fff;
        }
        `
    ]
})
export class NzDemoCarouselPositionComponent {
    array = [1, 2, 3, 4];
    dotPosition = 'bottom';
}

