collapseCustomCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-collapse-custom',
    template: `
        <nz-collapse [nzBordered]="false">
            <nz-collapse-panel
                #p
                *ngFor="let panel of panels; let isFirst = first"
                [nzHeader]="panel.name"
                [nzActive]="panel.active"
                [ngStyle]="panel.customStyle"
                [nzExpandedIcon]="!isFirst && (panel.icon || expandedIcon)"
            >
                <p>{{ panel.name }} content</p>
                <ng-template #expandedIcon let-active>
                    {{ active }}
                    <i nz-icon nzType="caret-right" class="ant-collapse-arrow" [nzRotate]="p.nzActive ? 90 : -90"></i>
                </ng-template>
            </nz-collapse-panel>
        </nz-collapse>
    `
})
export class NzDemoCollapseCustomComponent {
    panels = [
        {
            active: true,
            disabled: false,
            name: 'This is panel header 1',
            customStyle: {
            background: '#f7f7f7',
            'border-radius': '4px',
            'margin-bottom': '24px',
            border: '0px'
            }
        },
        {
            active: false,
            disabled: true,
            name: 'This is panel header 2',
            icon: 'double-right',
            customStyle: {
            background: '#f7f7f7',
            'border-radius': '4px',
            'margin-bottom': '24px',
            border: '0px'
            }
        },
        {
            active: false,
            disabled: false,
            name: 'This is panel header 3',
            customStyle: {
            background: '#f7f7f7',
            'border-radius': '4px',
            'margin-bottom': '24px',
            border: '0px'
            }
        }
    ];
}

