@Component({
    selector: 'nz-demo-cascader-default-value-and-lazyload',
    template: `
        <nz-cascader
            [(ngModel)]="values"
            [nzLoadData]="loadData"
            (ngModelChange)="onChanges($event)">
        </nz-cascader>
    `,
    styles  : [
    `
        .ant-cascader-picker {
            width: 300px;
        }
    `
    ]
})
