    suggestions = ['afc163', 'benjycui', 'yiminghe', 'اثممخ', '中文', 'にほんご'];

    onChange(value: string): void {
        console.log(value);
    }

    onSelect(suggestion: string): void {
        console.log(`onSelect ${suggestion}`);
    }
}

