import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'nz-demo-grid-flex-order',
    template: `
        <div nz-row nzType="flex">
            <div nz-col nzSpan="6" [nzOrder]="order" *ngFor="let order of orderList;index as i">
                {{ i + 1 }} col-order-{{ order }}
            </div>
        </div>
    `,
    styles  : []
})

export class NzDemoGridFlexOrderComponent implements OnInit {
    orderList = [ 1, 2, 3, 4 ];

    ngOnInit(): void {
        setTimeout(_ => {
        this.orderList = [ ...this.orderList.reverse() ];
        }, 10000);
    }
}

antGridPlaygroundCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-grid-playground',
    template: `
        <div class="m-b-15">
            <span class="m-r-5">Gutter (px): </span>
            <div nz-row>
                <div nz-col nz-col nzXs="24" nzSm="12">
                    <nz-slider [nzMarks]="marksGutter" [nzStep]="null" [nzDefaultValue]="16" [nzMax]="48" [(ngModel)]="gutter"></nz-slider>
                </div>
            </div>    
            <span class="m-r-5">Column Count:</span>
            <div nz-row>
                <div nz-col nz-col nzXs="24" nzSm="12">
                    <nz-slider [nzMarks]="marksCount" [nzStep]="null" [nzDefaultValue]="4" [nzMax]="12" [(ngModel)]="count"></nz-slider>
                </div>
            </div>
        </div>
        <div nz-row [nzGutter]="gutter">
            <div nz-col class="gutter-row" [nzSpan]="24/count" *ngFor="let i of generateArray(count)">
                <div class="grid-config">Column</div>
            </div>
        </div>
    `
})

export class NzDemoGridPlaygroundComponent {
    gutter = 16;
    count = 4;
    marksGutter = {
        8 : 8,
        16: 16,
        24: 24,
        32: 32,
        40: 40,
        48: 48
    };
    marksCount = {
        2 : 2,
        3 : 3,
        4 : 4,
        6 : 6,
        8 : 8,
        12: 12
    };

    generateArray(value: number): number[] {
        return new Array(value);
    }
}
