commentBasicCode:
import { Component } from '@angular/core';
import { distanceInWords } from 'date-fns';

@Component({
    selector: 'nz-demo-comment-basic',
    template: `
        <nz-comment nzAuthor="Han Solo" [nzDatetime]="time">
            <nz-avatar nz-comment-avatar nzIcon="user" nzSrc="//zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"></nz-avatar>
            <nz-comment-content>
                <p>We supply a series of design principles, practical patterns and high quality design resources (Sketch and Axure), to help people create their product prototypes beautifully and efficiently.</p>
            </nz-comment-content>
            <nz-comment-action>
                <i nz-tooltip nzTitle="Like" nz-icon nzType="like" [theme]="likes > 0 ? 'twotone' : 'outline'" (click)="like()"></i>
                <span class="pointer m-r-10 like">{{ likes }}</span>
            </nz-comment-action>
            <nz-comment-action>
                <i nz-tooltip nzTitle="Dislike" nz-icon nzType="dislike" [theme]="dislikes > 0 ? 'twotone' : 'outline'" (click)="dislike()"></i>
                <span class="pointer m-r-10 dislike">{{ dislikes }}</span>
            </nz-comment-action>
            <nz-comment-action>Reply to</nz-comment-action>
        </nz-comment>
    `
})
export class NzDemoCommentBasicComponent {
    likes = 0;
    dislikes = 0;
    time = distanceInWords(new Date(), new Date());

    like(): void {
        this.likes = 1;
        this.dislikes = 0;
    }

    dislike(): void {
        this.likes = 0;
        this.dislikes = 1;
    }
}

commentWithListCode:
import { Component } from '@angular/core';
import { addDays, distanceInWords } from 'date-fns';

@Component({
  selector: 'nz-demo-comment-list',
  template: `
    <nz-list [nzDataSource]="commentWithListData" [nzRenderItem]="item" [nzItemLayout]="'horizontal'">
        <ng-template #item let-item>
            <nz-comment [nzAuthor]="item.author" [nzDatetime]="item.datetime">
                <nz-avatar nz-comment-avatar nzIcon="user" [nzSrc]="item.avatar"></nz-avatar>
                <nz-comment-content>
                    <p>{{ item.content }}</p>
                </nz-comment-content>
                <nz-comment-action>Reply to</nz-comment-action>
            </nz-comment>
        </ng-template>
    </nz-list>
  `,
  styles: []
})
export class NzDemoCommentListComponent {
    data = [
        {
            author: 'Han Solo',
            avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
            content:
                'We supply a series of design principles, practical patterns and high quality design resources' +
                '(Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
            datetime: distanceInWords(new Date(), addDays(new Date(), 1))
        },
        {
            author: 'Han Solo',
            avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
            content:
                'We supply a series of design principles, practical patterns and high quality design resources' +
                '(Sketch and Axure), to help people create their product prototypes beautifully and efficiently.',
            datetime: distanceInWords(new Date(), addDays(new Date(), 2))
        }
    ];
}

commentReplayEditorCode:
import { Component } from '@angular/core';
import { distanceInWords } from 'date-fns';

@Component({
    selector: 'nz-demo-comment-editor',
    template: `
        <nz-list *ngIf="data.length" [nzDataSource]="data" [nzRenderItem]="item" [nzItemLayout]="'horizontal'">
            <ng-template #item let-item>
                <nz-comment [nzAuthor]="item.author" [nzDatetime]="item.displayTime">
                    <nz-avatar nz-comment-avatar nzIcon="user" [nzSrc]="item.avatar"></nz-avatar>
                    <nz-comment-content>
                        <p>{{ item.content }}</p>
                    </nz-comment-content>
                </nz-comment>
            </ng-template>
        </nz-list>
        <nz-comment>
            <nz-avatar nz-comment-avatar nzIcon="user" [nzSrc]="user.avatar"></nz-avatar>
            <nz-comment-content>
                <nz-form-item>
                    <textarea [(ngModel)]="inputValue" nz-input rows="4"></textarea>
                </nz-form-item>
                <nz-form-item>
                    <button nz-button nzType="primary" [nzLoading]="submitting" [disabled]="!inputValue" (click)="handleSubmit()">
                        Add Comment
                    </button>
                </nz-form-item>
            </nz-comment-content>
        </nz-comment>
    `,
    styles: []
})
export class NzDemoCommentEditorComponent {
    data: any[] = [];
    submitting = false;
    user = {
        author: 'Han Solo',
        avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png'
    };
    inputValue = '';

    handleSubmit(): void {
        this.submitting = true;
        const content = this.inputValue;
        this.inputValue = '';
        setTimeout(() => {
            this.submitting = false;
            this.data = [
                ...this.data,
                {
                    ...this.user,
                    content,
                    datetime: new Date(),
                    displayTime: distanceInWords(new Date(), new Date())
                }
            ].map(e => {
                return {
                    ...e,
                    displayTime: distanceInWords(new Date(), e.datetime)
                };
            });
        }, 800);
    }
}
