inputNumberBasicCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-input-number-basic',
    template: `
        <nz-input-number [(ngModel)]="demoValue" [nzMin]="1" [nzMax]="10" [nzStep]="1"></nz-input-number>
    `
})
export class NzDemoInputNumberBasicComponent {
    demoValue = 3;
}

inputNumberSizeCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-input-number-size',
    template: `
        <nz-input-number [(ngModel)]="demoValue" [nzSize]="'large'" [nzMin]="1" [nzMax]="10" [nzStep]="1"></nz-input-number>
        <nz-input-number [(ngModel)]="demoValue" [nzMin]="1" [nzMax]="10" [nzStep]="1"></nz-input-number>
        <nz-input-number [(ngModel)]="demoValue" [nzSize]="'small'" [nzMin]="1" [nzMax]="10" [nzStep]="1"></nz-input-number>`
})
export class NzDemoInputNumberSizeComponent {
    demoValue = 3;
}

inputNumberDisabledCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-input-number-disabled',
    template: `
    <nz-input-number class="m-r-10" [(ngModel)]="demoValue" [nzMin]="1" [nzMax]="10" [nzStep]="1" [nzDisabled]="isDisabled"></nz-input-number>
    <button nz-button [nzType]="'primary'" (click)="toggleDisabled()">
        <span>Toggle Disabled</span>
    </button>`
})
export class NzDemoInputNumberDisabledComponent {
    demoValue = 3;
    isDisabled = false;

    toggleDisabled(): void {
        this.isDisabled = !this.isDisabled;
    }
}

inputNumberDecimalsCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-input-number-digit',
    template: `<nz-input-number [(ngModel)]="demoValue" [nzMin]="1" [nzMax]="10" [nzStep]="0.1" [nzPlaceHolder]="'Digital'"></nz-input-number>`
})
export class NzDemoInputNumberDigitComponent {
    demoValue: number;
}

inputNumberFormaterCode:
import { Component } from '@angular/core';

@Component({
    selector: 'nz-demo-input-number-formatter',
    template: `
        <nz-input-number class="m-r-10" [(ngModel)]="demoValue" [nzMin]="1" [nzMax]="100" [nzStep]="1" [nzFormatter]="formatterDollar" [nzParser]="parserDollar"></nz-input-number>
        <nz-input-number [(ngModel)]="demoValue" [nzMin]="1" [nzMax]="100" [nzStep]="1" [nzFormatter]="formatterPercent" [nzParser]="parserPercent"></nz-input-number>
    `
})
export class NzDemoInputNumberFormatterComponent {
    demoValue = 100;
    formatterPercent = value => `${value} %`;
    parserPercent = value => value.replace(' %', '');
    formatterDollar = value => `$ ${value}`;
    parserDollar = value => value.replace('$ ', '');
}
